import React, { useState, useEffect } from "react";
import { API_URL } from "../../api";
import FeaturedImage from "../../components/featuredimage/featuredimage";

const WordpressProjects = () => {
  const [projects, setProjects] = useState([]);
  const [currentProject, setCurrentProject] = useState(null);
  const [categories, setCategories] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchProjects = async (category = "") => {
    setLoading(true);
    await fetch(API_URL + `projects${category}`)
      .then((response) => response.json())
      .then((result) => {
        setProjects(result);
        setLoading(false);
      });
  };

  useEffect(() => {
    const handleScroll = () => {
      projects.forEach((project) => {
        const image = document.getElementById(`project-image-${project.id}`);
        if (image.getBoundingClientRect().top < 400) {
          setCurrentProject(project);
        }
      });
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [projects]);

  return (
    <div>
      <h2>Projects</h2>
      <button className="filter-button" onClick={() => fetchProjects()}>
        All Projects
      </button>
      {categories.map((category) => (
        <button
          className="filter-button"
          key={category.id}
          onClick={() => fetchProjects(`?categories=${category.id}`)}
        >
          {category.name}
        </button>
      ))}

      {projects.map((project) => (
        <div key={project.id}>
          <div id={`project-image-${project.id}`}>
            <FeaturedImage
              pageId={project.id}
              type="projects"
              project={currentProject}
            />
          </div>
        </div>
      ))}
      <h2>{currentProject ? currentProject.title.rendered : "Loading..."}</h2>
    </div>
  );
};

export default WordpressProjects;
