import React, { useState, useEffect } from "react";
import { API_URL } from "../../api";
import FeaturedImage from "../../components/featuredimage/featuredimage";

const ProjectCarousel = () => {
  const [projects, setProjects] = useState([]);
  const [currentProjectIndex, setCurrentProjectIndex] = useState(0);
  const [currentProjectTitle, setCurrentProjectTitle] = useState("");

  useEffect(() => {
    fetch(API_URL + "projects")
      .then((response) => response.json())
      .then((data) => {
        setProjects(data);
      });
  }, []);

  useEffect(() => {
    setCurrentProjectTitle(projects[currentProjectIndex]?.title?.rendered);
  }, [currentProjectIndex, projects]);

  const handleScroll = (e) => {
    const projectImages = e.currentTarget.children;
    for (let i = 0; i < projectImages.length; i++) {
      const image = projectImages[i];
      const rect = image.getBoundingClientRect();
      if (rect.top >= 0 && rect.bottom <= window.innerHeight) {
        setCurrentProjectIndex(i);
        break;
      }
    }
  };

  return (
    <div className="project-carousel" onScroll={handleScroll}>
      {projects.map((project) => (
        <FeaturedImage pageId={project.id} type="projects" />
      ))}
      <h2>{currentProjectTitle}</h2>
    </div>
  );
};

export default ProjectCarousel;
