import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { API_URL } from "../../api";

const Desc = () => {
  const [projectInfo, setProjectInfo] = useState(null);
  const { slug } = useParams();

  useEffect(() => {
    fetch(API_URL + `projects?slug=${slug}`)
      .then((response) => response.json())
      .then((result) => {
        let content = result.content.rendered;
        content = content.replace("<figure>", "");
        setProjectInfo(content);
      });
  }, [slug]);

  if (!projectInfo) {
    return null;
  }

  return (
    <div>
      <div dangerouslySetInnerHTML={{ __html: projectInfo }} />
    </div>
  );
};

export default Desc;
