import { Link } from "react-router-dom";
import React, { useState, useEffect } from "react";

const Descricao = (pageId) => {
  const [postText, setPostText] = useState("");

  useEffect(() => {
    fetch(
      `https://dw-c3fe4d.ingress-daribow.ewp.live/wp-json/wp/v2/projects/${pageId}`
    )
      .then((response) => response.json())
      .then((data) => {
        let content = response.data.content.rendered;
        content = content.replace(/<figure[^>]*>/g, "");
        setPostText(content);
      });
  }, []);

  return (
    <div>
      <div dangerouslySetInnerHTML={{ __html: postText }} />
    </div>
  );
};
