import React, { useState, useEffect } from "react";
import FeaturedImage from "../../components/featuredimage/featuredimage";

const WordpressProjects = () => {
  const [projects, setProjects] = useState([]);
  const [currentProject, setCurrentProject] = useState(null);

  useEffect(() => {
    fetch(API_URL + `projects`)
      .then((response) => response.json())
      .then((data) => setProjects(data));
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      const currentProjectIndex = projects.findIndex((project) => {
        const image = document.getElementById(`project-image-${project.id}`);
        return image.getBoundingClientRect().top < 400;
      });
      setCurrentProject(projects[currentProjectIndex]);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [projects]);

  return (
    <div>
      {projects.map((project) => (
        <div key={project.id}>
          <div id={`project-image-${project.id}`}>
            <FeaturedImage pageId={project.id} type="projects" />
          </div>
        </div>
      ))}
      <h2>{currentProject ? currentProject.title.rendered : "Loading..."}</h2>
    </div>
  );
};

export default WordpressProjects;
