import React, { useState, useEffect, useRef } from "react";
import { API_URL } from "../../api";
import FeaturedImage from "../../components/featuredimage/featuredimage";

const Works = () => {
  const [projects, setProjects] = useState([]);
  const [currentProjectTitle, setCurrentProjectTitle] = useState("");
  const carouselRef = useRef(null);

  useEffect(() => {
    fetch(API_URL + "projects")
      .then((response) => response.json())
      .then((data) => {
        setProjects(data);
        setCurrentProjectTitle(data[0]?.title?.rendered);
      });
  }, []);

  useEffect(() => {
    carouselRef.current.addEventListener("scroll", handleScroll);
    return () => {
      carouselRef.current.removeEventListener("scroll", handleScroll);
    };
  }, [handleScroll]);

  const handleScroll = () => {
    const projectImages = carouselRef.current.children;
    for (let i = 0; i < projectImages.length; i++) {
      const image = projectImages[i];
      const rect = image.getBoundingClientRect();
      if (rect.top >= 0 && rect.bottom <= window.innerHeight) {
        setCurrentProjectTitle(projects[i]?.title?.rendered);
        break;
      }
    }
  };

  return (
    <div ref={carouselRef} className="project-carousel">
      {projects.map((project) => (
        <FeaturedImage pageId={project.id} type="projects" />
      ))}
      <h2>{currentProjectTitle}</h2>
    </div>
  );
};

export default Works;
