import React, { useState, useEffect, useRef } from "react";
import { API_URL } from "../../api";
import FeaturedImage from "../../components/featuredimage/featuredimage";

const ProjectCarousel = () => {
  const [title, setTitle] = useState("");
  const [projects, setProjects] = useState([]);
  const carouselRef = useRef(null);

  const handleScroll = () => {
    const element = carouselRef.current;
    const scroll = element.scrollLeft + element.offsetWidth;
    const projectsElements = element.querySelectorAll(".project");
    for (let i = 0; i < projectsElements.length; i++) {
      const project = projectsElements[i];
      const titleEl = project.querySelector(".title");
      if (titleEl.offsetLeft < scroll) {
        setTitle(titleEl.innerHTML);
      }
    }
  };

  useEffect(() => {
    fetch("https://dw-c3fe4d.ingress-daribow.ewp.live/wp-json/wp/v2/projects")
      .then((response) => response.json())
      .then((data) => {
        setProjects(data);
      });
    carouselRef.current.addEventListener("scroll", handleScroll);
    return () => {
      carouselRef.current.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="project-carousel-wrapper">
      <div ref={carouselRef} className="project-carousel">
        {projects.map((project) => (
          <div className="project" key={project.id}>
            <FeaturedImage pageId={project.id} type="projects" />
            <h2 className="title">{project.title.rendered}</h2>
          </div>
        ))}
      </div>
      <h2 className="current-title">{title}</h2>
    </div>
  );
};

export default ProjectCarousel;
